mapper.select:
SELECT post_id
FROM answers
WHERE id = #{answerId};

SELECT author_id
FROM answers
WHERE id = #{answerId}
  AND deleted_at IS NULL;

mapper.update:
UPDATE answers
SET deleted_at   = CURRENT_TIMESTAMP,
    deleted_type = 'SELF'
WHERE id = #{answerId}
  AND deleted_at IS NULL;

UPDATE answers
SET deleted_at   = CURRENT_TIMESTAMP,
    deleted_type = 'ADMIN'
WHERE id = #{answerId}
  AND deleted_at IS NULL;

UPDATE answers
SET deleted_at   = NULL,
    deleted_type = NULL
WHERE id = #{answerId}
  AND deleted_at IS NOT NULL
  AND deleted_type = 'ADMIN';
