mapper.update:
UPDATE posts
SET deleted_at = CURRENT_TIMESTAMP
WHERE id = #{postId};

UPDATE posts
SET deleted_at = NULL
WHERE id = #{postId};

mapper.select:
SELECT sub.post_id id, sub.title title, sub.anonymous anonymous, sub.author_id author_id, sub.author_nickname author_nickname, sub.views views, sub.likes likes, sub.answer_count answer_count, sub.created_at created_at, sub.modified_at modified_at, c.id category_id, c.name category_name, t.id tag_id, t.name tag_name FROM ( SELECT p.id post_id, p.title title, p.anonymous anonymous, p.user_id author_id, u.nickname author_nickname, p.category_id category_id, p.views views, p.likes likes, p.answer_count answer_count, p.created_at created_at, p.modified_at modified_at FROM posts p LEFT JOIN users u on u.id = p.user_id WHERE p.deleted_at IS NULL AND p.post_type = #{query.postType}
AND p.category_id = #{query.filterValue}
AND p.title LIKE concat('%', #{query.searchKeyword}, '%')
AND u.nickname LIKE concat('%',#{query.searchKeyword},'%') AND p.anonymous = FALSE
ORDER BY
p.created_at DESC p.views DESC p.likes DESC p.answer_count DESC p.created_at DESC
LIMIT #{pagination.offset}, #{pagination.size} ) sub LEFT JOIN category c ON c.id = sub.category_id LEFT JOIN post_tags pt ON pt.post_id = sub.post_id LEFT JOIN tags t ON t.id = pt.tag_id;

SELECT count(p.id) FROM posts p LEFT JOIN category c ON p.category_id = c.id LEFT JOIN users u ON p.user_id = u.id WHERE p.deleted_at IS NULL AND p.post_type = #{postType}
AND p.category_id = #{filterValue}
AND p.title LIKE concat('%', #{searchKeyword}, '%')
AND u.nickname LIKE concat('%',#{searchKeyword},'%') AND p.anonymous IS FALSE

SELECT exists(SELECT 1
              FROM post_likes
              WHERE user_id = #{authorId}
                AND post_id = #{postId});

mapper.update:
UPDATE posts
SET likes = likes + 1
WHERE id = #{postId};

UPDATE posts
SET views = views + 1
WHERE id = #{postId};

mapper.select:
SELECT COUNT(*)
FROM ((SELECT a.post_id
       FROM answers a
       WHERE author_id = #{userId}
         AND deleted_at IS NULL)
      UNION
      (SELECT c.post_id
       FROM comments c
       WHERE author_id = #{userId}
         AND deleted_at IS NULL)) reply_count;

SELECT count(p.id)
FROM posts p
WHERE p.user_id = #{userId}
  AND deleted_at IS NULL;

SELECT user_id
FROM posts
WHERE id = #{postId};
