mapper.select:
SELECT COUNT(r.id)
FROM reports r
LEFT JOIN users reporter ON r.reporter_id = reporter.id
WHERE r.deleted_at IS NULL


    AND r.status = #{status}



    AND r.target_type = #{targetType}



    AND r.created_at BETWEEN #{startDate} AND #{endDate}



    AND reporter.username LIKE CONCAT('%', #{keyword}, '%')

SELECT r.id, r.reporter_id, reporter.nickname as reporter_nickname, r.target_type, r.target_id, r.status, r.reason, r.detail_reason, r.created_at FROM reports r LEFT JOIN users reporter ON r.reporter_id = reporter.id WHERE r.deleted_at IS NULL
AND r.status = #{query.status}
AND r.target_type = #{query.targetType}
AND r.created_at BETWEEN #{query.startDate} AND #{query.endDate}
AND reporter.nickname LIKE CONCAT('%', #{query.keyword}, '%')
ORDER BY
r.created_at DESC
CASE WHEN r.status = 'PENDING' THEN 0 WHEN r.status = 'REVIEWING' THEN 1 ELSE 2 END, r.created_at DESC
r.created_at DESC
LIMIT #{pagination.size} OFFSET #{pagination.offset}

SELECT r.detail_reason
FROM reports r
WHERE id = #{reportId};

SELECT COUNT(r.id)
FROM reports r
WHERE deleted_at IS NULL;
