mapper.select:
select *
from users
where email = #{email}
  and deleted_at is null;

select password
from users
where email = #{email}
  and deleted_at is null;

select exists(select 1
              from users
              where email = #{email});

select exists(select 1
              from users
              where nickname = #{nickname}
                and deleted_at is null);

mapper.update:
update users
set deleted_at = CURRENT_TIMESTAMP
where id = #{id}
