mapper.select:
SELECT c.id id, c.post_id post_id, c.answer_id answer_id, c.author_id author_id, u.profile_image author_profile_image, u.nickname author_nickname, c.anonymous anonymous, c.content content, c.created_at created_at, c.deleted_at deleted_at FROM comments c LEFT JOIN users u ON c.author_id = u.id
WHERE c.answer_id IN
#{answerId}
AND c.deleted_at IS NULL;

SELECT author_id
FROM comments
WHERE id = #{commentId};

SELECT post_id
FROM comments
WHERE id = #{commentId};

mapper.delete:
UPDATE comments
SET deleted_at   = CURRENT_TIMESTAMP,
    deleted_type = 'SELF'
WHERE id = #{commentId}
  AND deleted_at IS NULL;

UPDATE comments
SET deleted_at   = CURRENT_TIMESTAMP,
    deleted_type = 'ADMIN'
WHERE id = #{commentId}
  AND deleted_at IS NULL;

mapper.update:
UPDATE comments
SET deleted_at   = NULL,
    deleted_type = NULL
WHERE id = #{commentId}
  AND deleted_at IS NOT NULL
  AND deleted_type = 'ADMIN';
