mapper.update:
UPDATE POSTS
SET deleted_at = current_timestamp
WHERE id = #{communityId};

mapper.select:
SELECT sub.post_id id, sub.title title, sub.anonymous anonymous, sub.author_id author_id, sub.author_nickname author_nickname, sub.content content, sub.views views, sub.likes likes, sub.answer_count answer_count, sub.created_at created_at, sub.modified_at modified_at, c.id category_id, c.name category_name, t.id tag_id, t.name tag_name FROM ( SELECT p.id post_id, p.title title, p.anonymous anonymous, p.user_id author_id, u.nickname author_nickname, p.content content, p.category_id category_id, p.views views, p.likes likes, p.answer_count answer_count, p.created_at created_at, p.modified_at modified_at FROM posts p LEFT JOIN users u on u.id = p.user_id WHERE p.deleted_at IS NULL AND p.post_type = #{param.postType}
AND p.category_id = #{param.filterValue}
AND p.title LIKE concat('%', #{param.searchKeyword}, '%')
AND u.nickname LIKE concat('%',#{param.searchKeyword},'%') AND p.anonymous = FALSE
ORDER BY
p.created_at DESC p.views DESC p.likes DESC p.answer_count DESC p.created_at DESC
LIMIT #{param.pageOffset}, #{param.pageSize} ) sub LEFT JOIN category c ON c.id = sub.category_id LEFT JOIN post_tags pt ON pt.post_id = sub.post_id LEFT JOIN tags t ON t.id = pt.tag_id;

SELECT count(p.id) FROM posts p LEFT JOIN category c ON p.category_id = c.id LEFT JOIN users u ON p.user_id = u.id WHERE p.deleted_at IS NULL AND p.post_type = #{param.postType}
AND p.category_id = #{param.filterValue}
AND p.title LIKE concat('%', #{param.searchKeyword}, '%')
AND u.nickname LIKE concat('%',#{param.searchKeyword},'%') AND p.anonymous IS FALSE

select exists(select 1
              from post_likes
              where user_id = #{authorId}
                and post_id = #{communityId});

mapper.update:
update posts
set likes = likes + 1
where id = #{communityId};

update posts
set views = views + 1
where id = #{communityId};
