mapper.update:
UPDATE posts
SET deleted_at = CURRENT_TIMESTAMP
WHERE id = #{noticeId};

mapper.select:
SELECT COUNT(p.id) FROM posts p WHERE p.deleted_at IS NULL AND p.post_type = 0
AND p.title LIKE CONCAT('%', #{searchKeyword}, '%')
AND p.user_id LIKE CONCAT('%', #{searchKeyword}, '%');

select EXISTS(select 1
              from post_likes
              where user_id = #{authorId}
                and post_id = #{noticeId});

mapper.update:
update posts
set likes = likes + 1
where id = #{noticeId};

update posts
set views = views + 1
where id = #{noticeId};
