mapper.update:
update posts
set deleted_at = current_timestamp
where id = #{noticeId};

mapper.select:
SELECT p.id id, p.title title, p.anonymous anonymous, p.user_id author_id, u.nickname author_nickname, p.content content, p.views views, p.likes likes, p.answer_count answer_count, p.created_at created_at, p.modified_at modified_at FROM posts p LEFT JOIN users u on u.id = p.user_id WHERE p.deleted_at IS NULL AND p.post_type = #{postType}
AND p.title LIKE concat('%', #{search.searchKeyword}, '%')
ORDER BY p.created_at desc LIMIT #{page.offset}, #{page.size}

select exists(select 1
              from post_likes
              where user_id = #{authorId}
                and post_id = #{noticeId});

mapper.update:
update posts
set likes = likes + 1
where id = #{noticeId};

update posts
set views = views + 1
where id = #{noticeId};
