mapper.select:
SELECT *
FROM users
WHERE email = #{email}
  AND deleted_at IS NULL;

SELECT password
FROM users
WHERE id = #{id}
  AND deleted_at IS NULL;

SELECT password
FROM users
WHERE email = #{email}
  AND deleted_at IS NULL;

SELECT EXISTS(SELECT 1
              FROM users
              WHERE email = #{email});

SELECT EXISTS(SELECT 1
              FROM users
              WHERE nickname = #{nickname}
                AND deleted_at IS NULL);

mapper.update:
UPDATE users
SET deleted_at = CURRENT_TIMESTAMP
WHERE id = #{id}
